mapper.select:
select t.id,t.pointId ,t.cardData,b.pointName 
  from card t ,point b  
where  t.pointId=b.id and t.id=#{id}


         select t.id,t.pointId ,t.cardData,b.pointName 
         from card t ,point b  
         where  t.pointId=b.id and t.pointId=#{pointId}

mapper.update:
   		 UPDATE card set
			   cardData=#{cardData},
			   updateTime=#{updateTime},
			   updateId=#{updateId}
	    where id=#{id}
