mapper.select:
SELECT a.id , a.pointName, a.sort, a.chapterId, a.chapterSort, a.number, a.bookId, b.state FROM errorwarehouse t ,point a,pointExerciseDetail b WHERE t.userId=#{userId} AND t.pointId= a.id AND t.pointId=b.pointId AND t.userId=b.userId AND t.bookId=a.bookId AND t.isRight=0 AND t.bookId=#{bookId}
and a.chapterId in
#{id}
GROUP BY t.bookId,t.pointId ORDER BY b.skilled,a.sort,a.id,t.createTime ASC

SELECT a.id , a.pointName, a.sort, a.chapterId, a.chapterSort, a.number, a.bookId, t.state FROM pointExerciseDetail t ,point a WHERE t.pointId=a.id AND t.bookId=a.bookId AND t.userId=#{userId} AND t.bookId=#{bookId}
and a.chapterId in
#{id}
AND t.exerciseCycle=0 AND t.skilled=0 AND a.number>0 ORDER BY a.sort,a.id ASC
